import './App.css';
import React, { useState, useEffect } from 'react';
import './chat.css'
import axios from 'axios';
// import toast from "bootstrap/js/src/toast";

function App() {
    // user_id will be taken from token in backend it just for testing purpose
    const [formData, setFormData] = useState({ name: "", url: "", extn: '', user_id: 2 });
    const [message, setMessage] = useState('');
    const [list, setList] = useState([]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    useEffect(async () => {
          const data = await axios.get('http://localhost:8080/movie-list');
          console.log(data);
    })
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8080/public/create", formData);
            setMessage(response?.data.message);
        } catch (error) {
            setMessage(error.message);
        }
    };

    return (
        <div>
            <h2>Submit Movie Details</h2>
            <form onSubmit={handleSubmit}>
                {message &&
                    <p>{message}</p>
                }

                <input type="text" name="name" placeholder="Movie Name" onChange={handleChange} />
                <input type="text" name="url" placeholder="Url" onChange={handleChange} />
                <input type="text" name="extn" placeholder="extn" onChange={handleChange} />
                <button type="submit">Save</button>
            </form>
        </div>
    );
}

export default App;
