import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './chat.css'

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState('');
    const [messages, setMessages] = useState('');
    const [userMessages, setUserMessages] = useState([]);
    const [isUserDisconnect, setIsUserDisconnect] = useState('');

    const sendMessage = (e) => {
        e.preventDefault();
        if(!messages){
            return;
        }
        socket.emit('send', { message: messages, id: window.localStorage.socket_id }, (err, response) => {
            if (err) {
                console.log(err);
            }
        });
    };

    useEffect(() => {
        const name = prompt("user Name!");
        if(window.localStorage?.socket.io){
            return ;
        }
        if(!name){
            alert("Please enter your name!");
        } else {
            socket.emit('new_user_joined', name);
            setChat(name);
        }
    }, []);

    useEffect(() => {
        socket.on('user_joined', (payload) => {
            window.localStorage.socket_id = payload.id;
        });

        socket.on('received', (payload) => {
            setMessages('')
            setUserMessages([...userMessages, payload]);
        });

        socket.on('disconnect', (payload) => {
            setIsUserDisconnect(`${payload?.name} has been disconnected!`);
        })
    })


    return (
        <div className="chat-container">
            <div className="chat-header">
                SimpleChat
                <span className="settings">⚙️</span>
            </div>
            <div className="chat-box">
                {userMessages.map((msg, index) => {
                    return (
                        <div className={`${msg.user_id !== window.localStorage.socket_id ? 'bot-message' : 'user-message'}`}><strong>{msg.name}</strong><br/>{msg.message}
                        </div>
                    )
                })}
                <div style={{textAlign: "center"}}>
                    {isUserDisconnect}
                </div>
            </div>
            <div className="chat-input">
                <input type="text"
                       placeholder="Enter your message..."
                       onChange={(e) => setMessages(e.target.value)
                }
                       value={messages}
                />
                <button onClick={(e) => {
                    sendMessage(e)
                }}>Send
                </button>
            </div>
        </div>
    );
}

export default App;
