import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { nanoid } from 'nanoid';

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState([]);
    const [messages, setMessages] = useState([
        { id: 1, text: "Hello! How are you?", sender: "other" },
        { id: 2, text: "I'm good! How about you?", sender: "me" },
    ]);
    const [input, setInput] = useState("");

    // const sendMessage = (e) => {
    //     e.preventDefault();
    //     socket.emit('chat', { message }, (err, response) => {
    //         if (err) {
    //             console.log(err);
    //         }
    //
    //         console.log(response);
    //     });
    // };
    //
    // useEffect(() => {
    //     socket.on('chat', (payload) => {
    //         setChat([...chat, payload]);
    //     });
    // });

    const sendMessage = () => {
        if (input.trim() === "") return;
        setMessages([...messages, { id: messages.length + 1, text: input, sender: "me" }]);
        setInput("");
    };


    return (
        <div className="flex h-screen bg-gray-100">
            {/* Sidebar */}
            <div className="w-1/4 bg-white p-4 shadow-md">
                <h2 className="text-lg font-semibold mb-4">Chats</h2>
                <div className="flex items-center gap-2 p-2 cursor-pointer hover:bg-gray-200 rounded-md">
                    <span className="text-gray-800">John Doe</span>
                </div>
            </div>

            {/* Chat Window */}
            <div className="flex flex-col flex-1">
                <div className="flex-1 overflow-y-auto p-4 bg-white shadow-md">
                    {messages.map((msg) => (
                        <div
                            key={msg.id}
                            className={`mb-2 p-2 rounded-lg max-w-xs ${msg.sender === "me" ? "bg-blue-500 text-white ml-auto" : "bg-gray-200 text-black"}`}
                        >
                            {msg.text}
                        </div>
                    ))}
                </div>

                {/* Message Input */}
                <div className="p-4 bg-white flex items-center gap-2 border-t">
                    <input
                        className="flex-1 border p-2 rounded-md"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Type a message..."
                    />
                    <button className="p-2 bg-blue-500 text-white rounded-md" onClick={sendMessage}>
                        Send
                    </button>
                </div>
            </div>
        </div>
    );
}

export default App;
