import './App.css';
import React, { useState, useEffect } from 'react';
import './chat.css'
import axios from 'axios';
import toast from "bootstrap/js/src/toast";

function App() {
    const [formData, setFormData] = useState({ name: "", url: "", extn: '' });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8080/public/create", formData);
            if(response?.status === 200){
                toast.success(response?.message);
            } else if(response?.status === 400){
                toast.error(response?.message);
            }
        } catch (error) {
            toast.error(error?.message);
        }
    };

    return (
        <div>
            <h2>Submit Movie Details</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" name="name" placeholder="Movie Name" onChange={handleChange} />
                <input type="text" name="url" placeholder="Url" onChange={handleChange} />
                <input type="text" name="extn" placeholder="extn" onChange={handleChange} />
                <button type="submit">Save</button>
            </form>
        </div>
    );
}

export default App;
