import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './chat.css'

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState('');
        const [messages, setMessages] = useState('');

    const sendMessage = (e) => {
        e.preventDefault();
        if(!messages){
            return;
        }
        socket.emit('send', { message: messages }, (err, response) => {
            if (err) {
                console.log(err);
            }

            setMessages('')
            socket.on('received', (payload) => {
                console.log("received", payload);
            })
        });
    };

    useEffect(() => {
        const name = prompt("user Name!");
        if(!name){
            alert("Please enter your name!");
        } else {
            socket.emit('new_user_joined', name);
            setChat(name);
        }
    }, []);

    useEffect(() => {
        socket.on('user_joined', (payload) => {
            console.log('User joined:', payload);
        });
    })

    return (
        <div className="chat-container">
            <div className="chat-header">
                SimpleChat
                <span className="settings">‚öôÔ∏è</span>
            </div>
            <div className="chat-box">
                <div className="bot-message"><strong>BOT</strong><br/>Hi, welcome to SimpleChat! Go ahead and send me a
                    message. üòä
                </div>
                <div className="user-message"><strong>Sajad</strong><br/>You can change your name in JS section!</div>
            </div>
            <div className="justify-content-around center-align">
                {chat} is joined
            </div>
            <div className="chat-input">
                <input type="text"
                       placeholder="Enter your message..." onChange={(e) => setMessages(e.target.value)}
                />
                <button onClick={(e) => {
                    sendMessage(e)
                }}>Send
                </button>
            </div>
        </div>
    );
}

export default App;
