import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { nanoid } from 'nanoid';

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState([]);
    const [messages, setMessages] = useState([
        { id: 1, text: "Hello! How are you?", sender: "other" },
        { id: 2, text: "I'm good! How about you?", sender: "me" },
    ]);
    const [input, setInput] = useState("");

    // const sendMessage = (e) => {
    //     e.preventDefault();
    //     socket.emit('chat', { message }, (err, response) => {
    //         if (err) {
    //             console.log(err);
    //         }
    //
    //         console.log(response);
    //     });
    // };
    //
    // useEffect(() => {
    //     socket.on('chat', (payload) => {
    //         setChat([...chat, payload]);
    //     });
    // });

    const sendMessage = () => {
        if (input.trim() === "") return;
        setMessages([...messages, { id: messages.length + 1, text: input, sender: "me" }]);
        setInput("");
    };


    return (
        <div className="App">
            <header className="App-header">
                <h1>Chatty Application</h1>
                {chat.map((item, index) => {
                    return (
                        <div className="App-item" key={index}>{item.message}</div>
                    );
                })}
                <form onSubmit={e => sendMessage(e)}>
                    <input type="text"
                           placeholder="type message"
                           name="user_message"
                           value={message}
                           onChange={(e) => {
                               setMessage(e.target.value);
                           }} />
                    <button type="submit">Send</button>
                </form>
            </header>
        </div>
    );
}

export default App;
