import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './chat.css'

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState('');
    const [messages, setMessages] = useState('');
    const [userMessages, setUserMessages] = useState([]);

    const sendMessage = (e) => {
        e.preventDefault();
        if(!messages){
            return;
        }
        socket.emit('send', { message: messages, id: window.localStorage.socket_id }, (err, response) => {
            if (err) {
                console.log(err);
            }

            setMessages('')
            socket.on('received', (payload) => {
                console.log("received", payload);
            });
        });
    };

    useEffect(() => {
        if(window.localStorage?.socket_id){
            return ;
        }
        const name = prompt("user Name!");
        console.log(name);
        if(!name){
            alert("Please enter your name!");
        } else {
            socket.emit('new_user_joined', name);
            setChat(name);
        }
    }, []);

    useEffect(() => {
        socket.on('user_joined', (payload) => {
            window.localStorage.socket_id = payload.id;
        });

        socket.on('received', (payload) => {
            setUserMessages([...userMessages, payload]);
        });
    })


    return (
        <div className="chat-container">
            <div className="chat-header">
                SimpleChat
                <span className="settings">⚙️</span>
            </div>
            <div className="chat-box">
                {userMessages.map((msg, index) => {
                    return (
                        <>
                            {msg.user_id !== window.localStorage.socket_id ?
                            <div className="bot-message"><strong>{msg.name}</strong><br/>{msg.message}
                            </div> :
                            <div className="user-message"><strong>{msg.name}</strong><br/>{msg.message}
                            </div>
                            }
                        </>
                    )
                })}

            </div>
            {/*<div className="justify-content-around center-align">*/}
            {/*    {chat} is joined*/}
            {/*</div>*/}
            <div className="chat-input">
                <input type="text"
                       placeholder="Enter your message..." onChange={(e) => setMessages(e.target.value)}
                />
                <button onClick={(e) => {
                    sendMessage(e)
                }}>Send
                </button>
            </div>
        </div>
    );
}

export default App;
