    //
    //     socket.on('left', (payload) => {
    //         console.log("left", payload);
    //         setIsUserDisconnect(`${payload?.name} has been disconnected!`);
    //     })
    // })

    const handlePostRequest = async (e) => {
        e.preventDefault();
        await fetch('http://localhost:8080/public/sign-up', {
            method: 'POST',
            credentials: 'include', // Required to send cookies or authentication headers
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({email: 'test@example.com', password: '123456'}),
        });
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                SimpleChat
                <span className="settings">⚙️</span>
            </div>
            <div className="chat-box">
                {userMessages.map((msg, index) => {
                    return (
                        <div className={`${msg.user_id !== window.localStorage.socket_id ? 'bot-message' : 'user-message'}`}><strong>{msg.name}</strong><br/>{msg.message}
                        </div>
                    )
                })}
                <div style={{textAlign: "center"}}>
                    {isUserDisconnect}
                </div>
            </div>
            <div className="chat-input">
                <input type="text"
                       placeholder="Enter your message..."
                       onChange={(e) => setMessages(e.target.value)
                }
                       value={messages}
                />
                <button onClick={(e) => {
                    handlePostRequest(e)
                }}>Send
                </button>
            </div>
        </div>
    );
}

export default App;
