import './App.css';
import React, { useState, useEffect } from 'react';
import './chat.css'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    // user_id will be taken from token in backend it just for testing purpose
    const [formData, setFormData] = useState({ name: "", url: "", extn: '', user_id: 2 });
    const [message, setMessage] = useState('');
    const [list, setList] = useState([]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    useEffect( () => {
        (async () => {
            const movieList = await axios.get('http://localhost:8080/public/movie-list');
            if(movieList && movieList?.data && movieList?.data?.data){
                setList(movieList?.data?.data);
            }
        })()
    })
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8080/public/create", formData);
            setMessage(response?.data.message);
        } catch (error) {
            setMessage(error.message);
        }
    };


    const handleDelete = async (e) => {
        e.preventDefault();
    }
    return (
        <div>
            <h2>Submit Movie Details</h2>
            <form onSubmit={handleSubmit}>
                {message &&
                    <p>{message}</p>
                }
                <div className="form">
                    <div className="form-group">
                        <input type="text" name="name" placeholder="Movie Name" onChange={handleChange}/>
                        <input type="text" name="url" placeholder="Url" onChange={handleChange}/>
                        <input type="text" name="extn" placeholder="extn" onChange={handleChange}/>
                    </div>

                </div>

                <button type="submit">Save</button>
            </form>

            <div className="card">
                <div className="card-header">Movie List</div>
                <div className="card-body">
                <div className="row">
                        {list && list.map((item, i) => {
                            return (
                                <>
                                    <div className="col-md-6">
                                        <ul>
                                            <li>
                                                {item.name}
                                            </li>
                                            <li>
                                                {item.url}
                                            </li>
                                            <li>
                                                {item.extn}
                                            </li>
                                            <li>
                                                <button className="btn btn-danger">Delete</button>
                                            </li>
                                        </ul>
                                    </div>
                                </>
                            )
                        })}

                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
