import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './chat.css'

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState('');
    const [messages, setMessages] = useState([]);

    const sendMessage = (e) => {
        e.preventDefault();
        socket.emit('chat', { message }, (err, response) => {
            if (err) {
                console.log(err);
            }

            console.log(response);
        });
    };

    useEffect(() => {
        const name = prompt("user Name!");
        if(!name){
            alert("Please enter your name!");
        } else {
            socket.emit('new_user_joined', name);
            setChat(name);
        }
    }, []);

    useEffect(() => {
        const handleUserJoined = (payload) => {
            console.log('User joined:', payload);
        };

        socket.on('user_joined', handleUserJoined);

        return () => {
            socket.off('user_joined', handleUserJoined);
        };
    }, [chat]);

    return (
        <div className="chat-container">
            <div className="chat-header">
                SimpleChat
                <span className="settings">‚öôÔ∏è</span>
            </div>
            <div className="chat-box">
                <div className="text-center">
                    {chat} is joined
                </div>
                <div className="bot-message"><strong>BOT</strong><br/>Hi, welcome to SimpleChat! Go ahead and send me a
                    message. üòä
                </div>
                <div className="user-message"><strong>Sajad</strong><br/>You can change your name in JS section!</div>
            </div>
            <div className="chat-input">
                <input type="text" placeholder="Enter your message..."/>
                <button onClick={(e) => {sendMessage(e)}}>Send</button>
            </div>
        </div>
    );
}

export default App;
