import './App.css';
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { nanoid } from 'nanoid';

const socket = io.connect('http://localhost:8080');

function App() {
    const [message, setMessage] = useState('');
    const [chat, setChat] = useState([]);
    const [messages, setMessages] = useState([
        { id: 1, text: "Hello! How are you?", sender: "other" },
        { id: 2, text: "I'm good! How about you?", sender: "me" },
    ]);
    const [input, setInput] = useState("");

    // const sendMessage = (e) => {
    //     e.preventDefault();
    //     socket.emit('chat', { message }, (err, response) => {
    //         if (err) {
    //             console.log(err);
    //         }
    //
    //         console.log(response);
    //     });
    // };
    //
    // useEffect(() => {
    //     socket.on('chat', (payload) => {
    //         setChat([...chat, payload]);
    //     });
    // });

    const sendMessage = () => {
        if (input.trim() === "") return;
        setMessages([...messages, { id: messages.length + 1, text: input, sender: "me" }]);
        setInput("");
    };


    return (
        <section className="msger">
            <header className="msger-header">
                <div className="msger-header-title">
                    <i className="fas fa-comment-alt"></i> SimpleChat
                </div>
                <div className="msger-header-options">
                    <span><i className="fas fa-cog"></i></span>
                </div>
            </header>

            <main className="msger-chat">
                <div className="msg left-msg">
                    <div
                        className="msg-img"
                        style="background-image: url(https://image.flaticon.com/icons/svg/327/327779.svg)"
                    ></div>

                    <div className="msg-bubble">
                        <div className="msg-info">
                            <div className="msg-info-name">BOT</div>
                            <div className="msg-info-time">12:45</div>
                        </div>

                        <div className="msg-text">
                            Hi, welcome to SimpleChat! Go ahead and send me a message. ğŸ˜„
                        </div>
                    </div>
                </div>

                <div className="msg right-msg">
                    <div
                        className="msg-img"
                        style="background-image: url(https://image.flaticon.com/icons/svg/145/145867.svg)"
                    ></div>

                    <div className="msg-bubble">
                        <div className="msg-info">
                            <div className="msg-info-name">Sajad</div>
                            <div className="msg-info-time">12:46</div>
                        </div>

                        <div className="msg-text">
                            You can change your name in JS section!
                        </div>
                    </div>
                </div>
            </main>

            <form className="msger-inputarea">
                <input type="text" className="msger-input" placeholder="Enter your message..."/>
                <button type="submit" className="msger-send-btn">Send</button>
            </form>
        </section>
    );
}

export default App;
